    "keywords": ["docker", "container", "dockerfile", "docker-compose", "image"],
    "rules": ["infrastructure/docker", "base/12-factor-app"]
  },
  "kubernetes": {
    "keywords": ["kubernetes", "k8s", "kubectl", "helm", "pod", "deployment"],
    "rules": ["infrastructure/kubernetes"]
  },
